mapper.select:
select ks.id as id, ks.para_code as paraCode, ks.para_value as paraValue, ks.para_type as paraType, ks.is_valid as isValid, ks.notes as note, ksty.id as TypeId, ksty.para_type as typeParaType, ksty.notes as typeNotes from ca_sys_code ks,ca_sys_param_type ksty where ks.para_type=ksty.id
and ks.id=#{id}
and ks.para_value = #{paraValue}
and ks.para_type=#{paraType.id}
